package week1.day1.examples;

public class ExampleSwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int j=3;
		
		switch(j){
		case 0:
			System.out.println(" The value of j is 0");
			break;
		case 1:
			System.out.println(" The Value of j is 1");
			break;
		case 2:
			System.out.println(" The Value of j is 2");
			break;
		case 3:
			System.out.println(" The Value of j is 3");
			break;
		 default:
			   System.out.println(" There is no value printed");
			   
			   
		}

	}

}
